package com.interview;

class Literal_Magic{
	


public static void main(String arg[]) {

	
		
	
	
		byte b1 = 10;
		byte b2 = 0b10;
		byte b3 = 0XA;
		byte b4 = 012;
		// TODO Auto-generated method stub
		System.out.println("as we take b1 as 10 "+b1);
		System.out.println("as we take b2 as 0b10 "+b2);
		System.out.println("as we take b3 as 0XA "+b3);
		System.out.println("as we take b4 as 012 "+b4);
	
	
	
	
}

}
